mapper.insert:
insert into cart (id, user_id, product_id, 
  quantity, `checked`, `status`, 
  deleted, gmt_create, gmt_modified
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{productId,jdbcType=INTEGER}, 
  #{quantity,jdbcType=INTEGER}, #{checked,jdbcType=INTEGER}, #{status,jdbcType=TINYINT}, 
  #{deleted,jdbcType=TINYINT}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(*)
from cart
where user_id=#{userId} and product_id=#{productId}
